export default {
    state: {
        todos: [],
    },
    mutations: {
        addTodo: state.todos.push(todo.content)
    },
    actions: {
        addTodo: ({commit}, todo) => {
            commit('addTodo', todo);
        },
    },
    getters: {
        todo: state => state.todos,
    }
}
